package com.bdt.service.impl;import java.util.List;import java.util.Map;import javax.annotation.Resource;import org.springframework.stereotype.Service;import com.bdt.dao.DAOBase;import com.bdt.dao.ArticleDAO;import com.bdt.entity.Article;import com.bdt.service.ArticleService;@Servicepublic class ArticleServiceImpl extends ServiceImplBase<Article, Long>        implements ArticleService {            @Resource    private ArticleDAO articleDAO;        @Override    protected DAOBase<Article, Long> getDAO() {        return articleDAO;    }    @Override    public List<Article> blurSearch(String article){        return articleDAO.blurSearch(article);    }    @Override    public List<Article> searchByPage(int n){        return articleDAO.searchByPage(n);    }}